#define FREQ 2 * 3.1415926
#define FEEDBACK_AMT 0.995

float sdCircle(vec2 uv, float rad){
    return length(uv)-rad;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 suv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    vec2 uuv = (fragCoord/iResolution.xy);

    float t = iTime*2.;

    float cir = sdCircle(suv-vec2(cos(t*.5), sin(t))*.4, .15);  // can shift with the -vec2
    vec3 col = step(cir, 0.) * vec3(0.3, 0.5, 0.6);  // 1 when below threshold(of 0) and 0 when above
    vec3 feedb = texture(iChannel0, uuv).xyz;

    col = max(col, feedb*FEEDBACK_AMT);

    fragColor = vec4(col,1.0);

}
