bool isInHill(vec2 posRatio) {
    return 0.2*cos(4.0*posRatio.x)+.4 > sin(posRatio.y);
}

vec3 drawHill(vec3 color, vec2 posRatio, float borderThickness) {
    if (isInHill(posRatio)) {
       color = vec3(0.0);
       posRatio.y += borderThickness;
       if (isInHill(posRatio))
          color = vec3(0.0,sin(.5*iTime)*sin(.5*iTime)+.2,0.3);
    }
    return color;
}

bool isInSun(vec2 posRatio, float aspectRatio, vec2 sunPos, float sunRadius) {
    return (distance(posRatio*vec2(1.0,aspectRatio), sunPos) < sunRadius);
}


vec3 drawSun(vec3 color, vec2 posRatio, float aspectRatio, vec2 sunPos, float sunRadius, float borderThickness) {
    // Sun
    if (isInSun(posRatio, aspectRatio, sunPos, sunRadius)) {
       color = vec3(0.0);
       sunRadius -= borderThickness;
       if (isInSun(posRatio, aspectRatio, sunPos, sunRadius))
           color = vec3(1.0,sin(.5*iTime)*sin(.5*iTime)+.5,0.0);
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 posRatio = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;

    // Sky
    vec3 color = mix(
        vec3(0.0,0.5,1.0), //Blue
        vec3(1.0,0.5,0.5),
        sqrt(posRatio.y)* cos(iTime)
    );

    // Sun
    vec2 sunPos = vec2(0.5,-0.1* cos(iTime) + 0.25);
    color = drawSun(color, posRatio, aspectRatio, sunPos, 0.1, 0.03);

    // Hill
    color = drawHill(color, posRatio, 0.06);

    fragColor = vec4(color,1.0);
}
