void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    //sort
    ivec2 c=ivec2(fragCoord);
    vec4 a=texelFetch(iChannel0,c,0);
    fragColor=a;
    vec4 b=texelFetch(iChannel0,c+ivec2(0,2*((c.y+iFrame)%2)-1),0);
    if(length(a)<1.4&&length(b)<1.4&&(length(a)>length(b)^^(c.y+iFrame)%2==0)) fragColor=b;


    //reset
    if(iMouse.z>0.0||iFrame<4) fragColor=texelFetch(iChannel1,c,0)+texelFetch(iChannel2,ivec2(c.x-1100,c.y%360),0);
}
