#define PI 3.1415
#define TWOPI 6.2831

float arctan( vec2 uv){
  float alfa = atan(uv.y/uv.x);
  if (uv.x > 0. && uv.y > 0.) return alfa;
  if (uv.x < 0. && uv.y > 0.) return PI - atan(uv.y/-uv.x);
  if (uv.x < 0. && uv.y < 0.) return  PI + atan(-uv.y/-uv.x);
  return TWOPI - atan(-uv.y/uv.x);
}


float ring(vec2 uv, float fase){
float intens;
float e = 0.01;
  float alfa = arctan(uv);
  float d = length(uv);
  d += .05*sin(5.*alfa);
  intens = smoothstep(.997, .99 + e, sin(8. * sqrt(d) - iTime + fase));
  intens*= smoothstep(.1, .1 + .1, d);
  return intens;

} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec3 col;
  float intens;
  float e = .01;
  vec2 offset;
  vec2 uv = (fragCoord.xy - .5*iResolution.xy)/iResolution.y;

  float aantal = 50.;
  for(float i = 1.; i < aantal+1.; i++){
    offset = vec2(.1 * sin( i * TWOPI / aantal), .1 * cos( i * TWOPI / aantal));
    intens = ring(uv - offset, i * TWOPI / aantal);
    col += .5*vec3(intens*abs(sin(.5*i)), intens*abs(sin(.13*i)), intens*abs(sin(.15*i)));
  }





  fragColor = vec4(col,1.0);
}
