float sdCircle(vec2 uv, float r)
{
    return length(uv) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;
    vec2 t = vec2(.3*sin(iTime),.3*cos(iTime)); 

    vec4 col = vec4(0.);
    float d1 = sdCircle(uv, .2);
    col = d1 > 0. ? texture(iChannel0, fragCoord/iResolution.xy) : vec4(0.);

    float d2 = sdCircle(uv - t, .2);
    t.x /= iResolution.x / iResolution.y;
    col = d2 > 0. ? col : texture(iChannel0, fragCoord/iResolution.xy - t);

    fragColor = col;
}
