vec3 palette( float t )
{
    vec3 a = vec3(0.407, 0.976, 0.380);
    vec3 b = vec3(0.476, 0.631, 0.464);
    vec3 c = vec3(0.220, 0.078, 0.394);
    vec3 d = vec3(0.077, 0.489, 3.816);

    return a + b*cos( 6.28318 * (c * t + d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    //uv = uv * 5.5;

    vec3 finalColor = vec3(0.0);

    float iterations = 14.0;
    for (float i = 0.0; i < iterations; i++)
    {
        uv = fract(uv * 1.5) - 0.5;
        vec3 col = palette(iTime * i);
        float x = length(uv);
        float v = clamp(x / tan(iTime * 0.3), 0.2, 0.8);
        //v = pow(v, 2.0);
        finalColor += v * col / iterations;
    }

    //finalColor = pow(finalColor, 1.3);

    fragColor = vec4(finalColor, 1.0);
}
