void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = vec3(uv.x, (cos(iTime) + 1.0) * 0.5, uv.y);
    float t = (sin(iTime) + 1.0) * 0.2;
    float d = float(uv.y > t * sin(uv.x * 10.0F + iTime) + 0.5);
    color = mix(color, vec3(uv.y, uv.x, (sin(iTime) + iMouse.x) * 0.5), d);
    fragColor = vec4(color, 1.0);
}
