void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
  float t = 0.5 - 0.5 * cos(iTime * 0.2);
  uv = uv * pow(0.5, t * 13.);

  vec2 z = vec2(0);
  vec2 c = uv + vec2(-0.731, 0.21015);
  float total = 200.;
  float count = 0.;

  while(count <= total && length(z) < 2.) {
    // f(x) = z^2 + c = (x+iy)^2 + c = x^2 - y^2 + 2xyi + c
    z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
    count++;
  }

  vec3 col = vec3(0);
  col += count / total;

  fragColor = vec4(col, 1.0);
}
