// source: https://iquilezles.org/articles/distfunctions2d/
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// source: https://iquilezles.org/articles/palettes/
vec3 palette( in float t )
{
    vec3 a = vec3(0.260, 0.708, 0.048);
    vec3 b = vec3(0.498, 1.278, 0.668);
    vec3 c = vec3(-0.692, 0.588, 0.248);
    vec3 d = vec3(-0.672, 0.218, -0.112);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvC = uv * 2. - 1.0;
    vec2 uvM = iMouse.xy / iResolution.xy;
    float distanceC = length(uvC);
    float distanceM = length(uvM - uv);
    float timeSin = sin(iTime);

    vec2 box = vec2(.5, .5);

    float time = iTime*16.;

    float thing = abs(sin(sdBox(uvC/2., box)*8. + time)/8.);

    float c = step(0.09, thing);

    vec2 box2 = vec2(.1, .1);

    float time2 = iTime*2.;

    float thing2 = abs(sin(sdBox(uvC/16., box2)*16. - time2)/16.);

    float c2 = step(0.05, thing2);

    //c = 1./c;

    vec3 col = vec3(palette(c-c2));

    fragColor = vec4(col,1.0);

    float x1 = smoothstep(0., 0.02, abs(uvC.y - uvC.x))*0.07;
    float x2 = smoothstep(0., 0.02, abs(uvC.x + uvC.y))*0.07;
    fragColor = vec4(palette(x1*4.)+palette(x2*4.)+col, 1.0);

}
