// Original Palettes by iq
// https://www.shadertoy.com/view/ll2GD3
// https://iquilezles.org/articles/palettes
#define PI 3.141592

vec3 pal(float progress, vec3 scale, vec3 offset, vec3 minC, vec3 maxC)
{
    return (cos((progress * scale + offset + 1.0) * PI) * 0.5 + 0.5) * (maxC - minC) + minC;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0);
    col = pal(uv.x, vec3(0.4, 2.5, 2.5), vec3(0.0, 0.4, -0.0), vec3(0.25,0.0,0.2), vec3(1.3,0.5,1.0));
    vec3 col1 = col;

    //debug separate channels
    if (uv.y < 0.5) col = col1.rrr * vec3(1,0,0);
    if (uv.y < 0.4) col = col1.ggg * vec3(0,1,0);
    if (uv.y < 0.3) col = col1.bbb * vec3(0,0,1);

    if (uv.y < 0.2) col = pal(uv.x, vec3(1.0), vec3(0), vec3(0,0,0), vec3(1,1,1)); // defaul values, start here
    if (uv.y < 0.1) col = vec3(uv.x); //progress

    //dithering
    col += (1.0/255.0)*texture(iChannel0, fragCoord.xy/iChannelResolution[0].xy).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
