void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float duration = 1.7;
    float maxScale = 1.1;
    float pixelOffset = 0.02;

    float progress = mod(iTime, duration) / duration;
    vec2 offset = vec2(pixelOffset, pixelOffset) * progress;
    float scale = 1.0 + (maxScale - 1.0) * progress; 

    // color offset and scale offset
    vec2 uv = fragCoord/iResolution.xy;
    vec2 editUv = (uv-0.5) / scale + 0.5;

    vec4 colorR = texture(iChannel0, editUv + offset);
    vec4 colorG = texture(iChannel0, editUv - offset);
    vec4 colorB = texture(iChannel0, editUv);
    vec3 color = vec3(colorR.r, colorG.g, colorB.b);

    fragColor = vec4(color,1.0);
}
