float twist = 8.0;
float spirals = 20.0;
float speed = 2.0;
float pi = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x/iResolution.x-0.5, (fragCoord.y/iResolution.y-0.5)*iResolution.y/iResolution.x);
    if ( mod(spirals*atan( uv.y, uv.x ) + pi*(log(length(uv))/log(1.0+1.0/twist)/(1.0+1.0/twist)-speed*iTime), pi*2.0) <= pi ) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
