#define S(v) smoothstep(ss,-ss,v)

vec3 radial(vec3 col, vec2 p, float r1, vec3 c1, float r2, vec3 c2) {
    float ss = .75/iResolution.y;
    float l = length(p);
    col = mix(c2, col, S(r2-l));
    col = mix(c1, col, S(r1-l));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 p = uv;
    float ss = .75/iResolution.y;

    vec3 bg = vec3(.384,.188,.427);
    vec3 c1 = vec3(.969,.925,.49);
    vec3 c2 = vec3(.667,.267,.373);
    vec3 c3 = vec3(.89,.561,.4);

    vec3 col = abs(p.x) > .2 && abs(p.x) < .6 && p.y < -.2 || abs(p.x) < .2 && p.y < .2 ? c1 : bg;
    col = radial(col, p + vec2(0,-.2), .125, c2, .2, c3);
    col = radial(col, vec2(abs(p.x)-.4,p.y+.2), .125, c3, .2, c2);

    fragColor = vec4(col,1.0);
}
