void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+floor(uv.xyx*64.0)+vec3(0,2,4));

    float mult = 11.0;

    float audioMult = pow(texture(iChannel0, vec2(0.5)).x, 2.10);

    float audioSample = texture(iChannel0, vec2(fract(uv.x*audioMult*mult), floor(uv.y*mult*audioMult))).x;

    vec3 test = texture(iChannel1, vec2(uv.x + sin(audioSample * 0.05), uv.y - cos(audioSample * 0.25))).rgb;

    if(uv.y < audioMult) test *= mix(test*2.0, vec3(sin(audioSample * col.r), cos(audioSample * col.g), 1.0 - tan(audioSample * col.b)), (audioMult));
    // Output to screen
    fragColor = vec4(test,1.0);
}
