#define PI 3.14159265

// Same triangles and same rotations left/right and top/bottom
// Different space deformation

// color(t) = a + b ⋅ cos[ 2π(c⋅t+d)]
vec3 palette(float t)
{
    vec3 a = vec3(.5,.5,.5);
    vec3 b = vec3(.5,.5,.5);
    vec3 c = vec3(1.);
    vec3 d = vec3(.234, .345, .456);

    return a + b * cos(2.0*PI*(c*t + d));
}

// SDF -> IQ
float triangle(vec2 p, float r)
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;

    if(p.x+k*p.y>0.0) p = vec2(p.x-k*p.y, -k*p.x-p.y) / 2.0;

    p.x -= clamp(p.x, -2.0*r, 0.0);

    return -length(p)*sign(p.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);

    vec2 p2 = vec2(cos(p.x)*cos(p.y), 2.0*sin(p.x)*cos(p.y) );  

    for (int i = 0; i < 4; i++)
    {
    float an = iTime;
    float cc = cos(an);
    float ss = sin(an);

    p2 = mat2(cc, -ss, ss, cc) * p2;
    p = mat2(cc, -ss, ss, cc) * p;
    float d = triangle(p2, 0.8);

    p.x = abs(p.x);
    float d2 = triangle(p - vec2(1., 0.0), 0.3);

    d = max(pow(0.05 / d, 1.2), .1);
    d2 = max(pow(0.05 / d2, 1.2), .1);

    col += palette(d);
    col += palette(d2);

    }
    fragColor = vec4(col, 1.0);
}
