const vec3 GAMMA = vec3(2.2);

vec3 LinearFromSRGB(vec3 sRGB) { return pow(sRGB/255., GAMMA); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float pix_size = 2./iResolution.y;
    vec3 color = vec3(0);

    vec3 Green = LinearFromSRGB(vec3(34, 139, 34));
    vec3 BrightGreen = LinearFromSRGB(vec3(113, 158, 31));
    vec3 VeryDarkGreen = LinearFromSRGB(vec3(52, 67, 28));
    vec3 DarkGreen = LinearFromSRGB(vec3(59, 80, 22));

    vec3 LightSkyBlue = LinearFromSRGB(vec3(135, 206, 250));
    vec3 DeepSkyBlue = LinearFromSRGB(vec3(0, 191, 255));

    vec3 FullSkyBlue = LinearFromSRGB(vec3(37, 98, 232));
    vec3 SoftSkyBlue = LinearFromSRGB(vec3(118, 161, 241));
    vec3 CloudyWhite1 = LinearFromSRGB(vec3(225, 230, 243));
    vec3 CloudyWhite2 = LinearFromSRGB(vec3(236, 239, 243));
    vec3 CloudySky = LinearFromSRGB(vec3(157, 181, 241));

    vec3 Brown = LinearFromSRGB(vec3(135, 206, 250));

    float r = 1.;
    float feather = 0.0025;
    float disc = smoothstep(r+pix_size + feather, r, length(uv));

    vec3 SkyBlue = mix(SoftSkyBlue, FullSkyBlue, smoothstep(0., 0.75, dot(uv, vec2(0.3,1.))));

    color = SkyBlue;
    float elevation = mix(uv.y-0.1, uv.y+0.1, smoothstep(-1., 1., uv.x));

    vec3 HillGreen = mix(DarkGreen, BrightGreen, smoothstep(-.9, 0.1, uv.y));

    color = mix(color, HillGreen, smoothstep(pix_size, -2.*feather, elevation));

    vec2 cloudpos = uv - vec2(0.5, 0.5);
    float cloud = (.25*sin(uv.x*.4) + .75)*
    smoothstep(.3, 0., length(cloudpos*vec2(.2, .5) + .1*sin(cloudpos.x*3.) + .1*sin(cloudpos.y*3.)));
    color = mix(color, CloudyWhite1, cloud);

    color *= disc;
    color = pow(color, 1./GAMMA);
    fragColor = vec4(color, 1.0);
}
