/* @maxwellcoffee - 2023 + @kishimisu - 2022)
*/

#define a(a,b,f,o) mix(a,b,f*f+4.5)
#define s(a) mat2(tan(a*vec4(1,3,1,1)))

void mainImage(out vec4 O, vec2 F) {
    float c , 
          o , 
          e = (0.4+iTime*.021111111)*(1.5+0.001*tan(iTime / 10.));
    vec2  r = iResolution.xy; 

    for (;o++<1e2;) {
    /* substitute the 1e2 above to 
    1e1 for faster performance (such as full screen) or 
    1e3 for more detail (could make it choppy)
*/
        vec3 p = abs(.7*c*normalize(vec3((F+F-r)/r.y, 1)));
        p.xy *= s(e);
        p.zy += e + c*cos(6.*e);
        p = fract(p)-.15;
        p.xy *= s(c);
        p.xz *= s(e);
        p.y = max(abs(p.y)-a(0.,.2,1.,),0.);
        c += (length(vec2(length(p.xy)-.2,p.z)) 
                 -a(.14,.31,.25,14.) - c*.01)*.5;
    }

    O.rgb = 1.2*(tan(c*110. + .99*vec3(0,1.+c*.2,2))+.2)/exp(c*0.0914);
}
