vec2 hash( vec2 p ) {
	p = vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( vec2 p ) {
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;
	vec2 i = floor(p + (p.x+p.y)*K1);	
    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = (a.x>a.y) ? vec2(1.0,0.0) : vec2(0.0,1.0); //vec2 of = 0.5 + 0.5*vec2(sign(a.x-a.y), sign(a.y-a.x));
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;
    vec3 h = max(0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot(n, vec3(70.0));	
}

mat2 m = mat2(0.8f, 0.6f, -0.6f, 0.8f);
float fbm(vec2 p)
{
    float f = 0.0f;
    f += 0.5f*noise(p); p *= m*2.02f;
    f += 0.25f*noise(p); p *= m*2.03f;
    f += 0.125f*noise(p); p *= m*2.01f;
    f += 0.0625f*noise(p); p *= m*2.04f;
    f /= 0.9375f;
    return f;
}

//---------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord/iResolution.xy;
    vec2 p = 2.0f * q - 1.0f;
    p.x *= iResolution.x/iResolution.y;

    float r = sqrt(dot(p,p));
    float a = atan(p.y, p.x);

    float ss = 0.5f * (0.5f + 0.5f * sin(2.0f * iTime)) + 0.25f;
    float anim = 1.0f + 0.1f*ss;
    r *= anim;

    //float f = fbm(4.0f*p);

    vec3 col = vec3(1.0f);

    if(r < 0.8)
    {
        col = vec3(0.8f, 0.8f, 0.7f);
        float f = fbm(2.0f*p);
        col = mix(col, vec3(0.88f, 0.933f, 0.855f), f);

        f = 1.0f - smoothstep (0.2f, 0.5f, r);
        col = mix(col, vec3(0.6f, 0.5f,0.2f), f);

        a += 0.05f*fbm(8.0f*p);

        f = smoothstep(0.3f, 1.0f, fbm(vec2(1.5f*r, 5.0f*a)));
        f = fbm(vec2(1.5f*r, 5.0f*a));
        col = mix(col, vec3(1.0f), f);

        f = smoothstep(0.65f, 0.9f, r);
        col *= 1.0f - 0.7f*f;

        f = smoothstep(0.21f, 0.25f, r);
        col *= f;

        f = 1.0f - smoothstep(0.0f, 0.6f, length(p-vec2(0.24f, 0.20f)));
        col += 0.25f*vec3(f);

        f = smoothstep(0.75f, 0.80f, r);
        col = mix(col, vec3(1.0f), f);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
