void mainImage( out vec4 o, in vec2 i )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = i/iResolution.xy;
    uv-=.5;
    uv.y/=iResolution.x/iResolution.y;


    vec3 col =1.-vec3(abs((0.7+.5*cos(iTime))*22.-88.*length(uv)));
    col.rb+=1.*(1.-abs( .5-  fract( 10.*cos(iTime)+ (64.*atan(uv.x,uv.y)/6.283)    )    ));
   //col*=1.*(.5-abs( .5-  fract( 10.*cos(iTime)+ (52.*atan(uv.x,uv.y)/6.283)    )    ));


    // Output to screen
    o = vec4(col,1.0);
}
