#define PI 3.14159265358979323846

vec3 rotate(vec3 v, vec3 axis, float angle)
{
    vec3 res = v;
    res = res - dot(res, axis) * axis;
    res = res * cos(angle) + cross(axis, res) * sin(angle) + axis * dot(axis, res) * (1.0 - cos(angle));
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy / iResolution.y) * 2.0 - 1.0;
    vec3 r = vec3(p,0);
    vec3 ro = vec3(0,0,0);
    vec3 ta = vec3(0,0,0);
    float d = 0.0;
    vec3 c = vec3(0);
    float an = iTime*2.;
    mat2 m2 = mat2(cos(an),-sin(an),sin(an),cos(an));
    mat3 m3 = mat3(cos(an),-sin(an),0,sin(an),cos(an),0,0,0,1);

    // apply random rotation to the object
    float random_rotation = fract(sin(iTime) * 12.9898);
    vec3 random_axis = vec3(fract(sin(iTime) * 43758.5453), fract(cos(iTime) * 4376.5453), fract(sin(iTime + 1.0) * 43758.5453));
    r = rotate(r, normalize(random_axis), random_rotation * PI * 2.);

    for(int i=0;i<3;i++)
    {
        r = m3 * r;
        d = dot(r,r);
        if(d<0.05)
        {
            c = vec3(0,1,0);
            break;
        }
    }
    // apply linear dim green light
    vec3 light = normalize(vec3(0,1,1));
    float diffuse = dot(normalize(r), light);
    diffuse = pow(diffuse, 2.);
    fragColor = vec4(c*diffuse, 1);
}
