void mainImage(out vec4 outColor, vec2 fragCoord)
{
    outColor = vec4(0.);
    vec2 st = fragCoord.xy / iResolution.y/16.;

    vec2 b = vec2(0,.2), p;

    mat2 rotation;

    for(float i=.9; i<6.0 ;++i){
        rotation = mat2(cos(i + vec4(0,33,11,0)));
        st = rotation * (fract((vec2(st.x,st.y+iTime * (0.18)/8.) * (i))*rotation)-.5);
        outColor += 0.001/ length(clamp(st,-b,b)-st);
    }
}
