#define BitAmount 20.
#define Distortion .3
#define FlickerAmount .3
#define FlickerSpeed 5.
#define FlickerSize 3.

//from Dave_Hoskins
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    uv = mix(uv, uv * length(uv), Distortion);

    float Mix = 0.;
    for (float i = 0.; i < 10.; i++) {
        float Hash = floor(hash12(vec2((floor((uv + .5) * BitAmount) + vec2(0., floor(iTime * BitAmount) - i)) / BitAmount)) * 1.025);

        if (Hash == 1.) {
            Mix = 1. - i / 10.;
            break;
        }
    }

    vec3 ScanLines = vec3(.5, .2, .3) * (abs(fract(uv.y * 100.) - .5) + abs(fract(uv.y * FlickerSize + iTime * FlickerSpeed) - .5) * FlickerAmount);

    uv = floor(fract((uv + .5) * BitAmount) * 20.) * .05;

    vec4 Texture0 = texture(iChannel0, (uv + vec2(0., 12.)) / 16.);
    vec4 Texture1 = texture(iChannel0, (uv + vec2(1., 12.)) / 16.);

    float Num0 = Texture0.x;
    float Num1 = (1. - Texture1.w) * 1.5 + Texture1.x;

    vec3 Output = mix(vec3(.15, .05, .2) * Num0, vec3(.6, .2, .8) * Num1, Num1 * Mix);
    fragColor = vec4(mix(ScanLines, Output, .8), 1.);
}
