 #define time iTime
 #define R iResolution.xy


mat2 rot(float a)
{
    float ca = cos(a);
    float sa = sin(a);
    return mat2(ca,sa,-sa,ca);
}

float box(vec2 uv, vec2 size)
{
   float cx = step(abs(uv.x), size.x);   
   float cy = step(abs(uv.y), size.y);
    return min(cx, cy);
}

float rnd(float t)
{
    return fract(sin(t*714.112)*9334.527);
}

float Cir (vec2 uv, float r, bool blur) {
    float a = blur ? 0.001 : 1.;
    float b = blur ? 0.11 : 5./R.y;
    return smoothstep(a, b, length(uv)-r);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5);
    uv /= iResolution.y;    
    uv*= rot(time*1.);
    vec3 col = vec3(0);
    col += vec3(0.8,0.7,0.6);
     if(length(uv)<0.52){
    col = vec3(0,0,0);

    }

    for (float i=0.0; i<50.0; ++i)
{

vec2 uv2 = uv;
uv2*=rot(time*(0.03 + i*0.001) +i*0.1);
uv2.x +=rnd(i)*.07-.55;
float t=time +i*0.1;


float taille = rnd(floor(t*10.)+ i*0.3);
col -=box(uv2, vec2(0.01,0.5 * taille)) * vec3(0.4,0.4,0.4);
}


  if(length(uv)<0.5){
  float cir2B = Cir(uv,0.45, true);
vec3 Col = vec3(1,1,1);
col = mix(Col, col, cir2B);
    }


     if(length(uv)<0.34){
     float cir2B = Cir(uv,0.28, true);
 vec3 Col = vec3(0.,0.,0.);
 col = mix(Col, col, cir2B);
    }
  if(length(uv)<0.3){
  float cir2B = Cir(uv,0.23, true);
 vec3 Col = vec3(0.4,0.2,0.1);
col = mix(Col, col, cir2B);
    }

    if(length(uv)<0.2){
    float cir2B = Cir(uv,0.12, true);
  vec3 Col = vec3(0.5,0.4,0.2);
col = mix(Col, col, cir2B);
    }
    if(length(uv)<0.16){
    float cir2B = Cir(uv,0.06, true);
 vec3 Col = vec3(0.,0.,0.);
col = mix(Col, col, cir2B);
    }

for (float i=0.0; i<50.0; ++i)
{

vec2 uv2 = uv;
uv2*=rot(time*(0.03 + i*0.001) +i*0.4);
float t=time +i*0.1;

float taille = rnd(floor(t*1.)+ i*0.1);
col -=box(uv2, vec2(0.003,0.3 * taille)) * vec3(i*0.05,0.8,0.8);
}


    // Output to screen
    fragColor = vec4(col,1.0);
}
