#define PI 3.1415926

vec3 sdfCircle(vec2 uv, float r, vec2 offset) {
    float d = length(uv-offset) - r;

    return d > 0. ? vec3(1.) : vec3(0.,0.,1.);
}

vec3 sdfSquare(vec2 uv, float size, vec2 offset) {
    float d = max(abs(uv.x + offset.x), abs(uv.y + offset.y)) - size;
    return d > 0. ? vec3(1.) : vec3(1.,0.,0.);
}

vec2 rotate(vec2 uv, float th) {
    return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 offset = vec2(cos(iTime) * .2, sin(iTime) * .2);

    fragColor = vec4(sdfSquare(rotate(uv, PI/4.), .2, offset), 1.0);
}
