void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Defining textures and threshold data
    vec4 texcol1 = texture(iChannel1, uv);
    vec4 texcol0 = texture(iChannel0, uv);
    vec4 postCol = texcol0;
    float brightness = (texcol0.r + texcol0.g + texcol0.b) / 3.0;
    //Defining Ratio of green to other colors.
    float greenVal = texcol0.g / ((texcol0.r + texcol0.b) * 1.0);


    //Boolean function to determine what to blend
    if (greenVal > 0.8 && brightness > 0.1)
    {
        postCol = (texcol1 * 2.0 + mix (texcol1, texcol0, greenVal/20.0))/3.0;
        //postCol = vec4(texcol1);
        }

    // Output to screen
    fragColor = vec4(postCol);
    //fragColor = vec4(1.0 - greenVal / 2.0);

}
