void mainImage(out vec4 fragColor, vec2 fragCoord) {
    fragColor = nextMip(fragCoord, iResolution, iChannel0, 4);
}

vec4 nextMip(vec2 fragCoord, vec3 iResolution, sampler2D channel, int level) {
    int scale = 1 << level;
    fragCoord = vec2(ivec2(fragCoord - 0.5) / scale * scale + scale / 2);
    vec2 uv = fragCoord / iResolution.xy;
    return texture(channel, uv);
}
