float random( vec2 p ) {
    return fract(sin(dot(p,vec2(127.1,311.7)))*43758.5453);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec3 random3( float p ) {

    float R = fract(cos(p)*395534.2344);
    float G = fract(cos(p)*9845.34644);
    float B = fract(cos(p)*632634.543224);
    return vec3(R, G, B);
}

float Voronoi(vec2 st){
    vec2 stFl = floor(st);
    vec2 stFr = fract(st);

    float d_min = 1.;
    vec2 cell = vec2(1.);
    for(int x = -1; x <=1; x++){
        for(int y = -1; y<= 1; y++){

            vec2 neighbor = vec2(float(x), float (y));
            vec2 point = random2(neighbor + stFl);
            point = 0.5 + 0.5 * cos(iTime*0.5 + 6.345234*point);
            vec2 diff = point + neighbor - stFr;
            float d = length(diff);
            if(d < d_min){
                 d_min = d;
            	 cell = neighbor + stFl;
            }
        }
    }

    float random = random(cell);
    return random;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;

    st *= 8.;
    float voronoi = Voronoi(st);

    vec3 B = vec3(voronoi);
    vec3 Col = vec3(random3(voronoi));

    vec3 color = vec3(0.);
    color =mix(Col, B, cos(iTime*0.5));

    color += texture(iChannel0, vec2(1)).rgb;



    fragColor = vec4(color,1.0);
}
