void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;


    O = vec4(smoothstep(1./r.y, 0., abs(texture(iChannel0, vec2(uv.x, 0)).x-uv.y)));

    // Smoothstep is an intentional design choice to give randomized brightness to particles
}

const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

void mainImage( out vec4 O, vec2 U ){
    if (U.y == .5){
         if (iFrame < 20){
             O = vec4(0);
             O.x = hash(uvec3(U.x, iDate.w, iDate.z)).x;
         } else {
             O = texture(iChannel0, U/iResolution.xy);

             // Assuming we have a GPU, this is a naïve method but it works
             // Sort here

             bool d = (int(U.x) + iFrame)%2 == 1;

             float v = texelFetch(iChannel0, ivec2(U.x + (d?1.:-1.) - .5, 0), 0).x;

             if (v == 0.) return;

             O.x = (v < O.x && d) || (v > O.x && !d)?v:O.x;


         }
    } else {
        O = vec4(0);
    }
}
