vec4 m0 = vec4(0.0184, 0.0312, 0.134, 1.149);
vec4 m1 = vec4(0.0323, 0.0634, 0.123, -0.443);
vec4 m2 = vec4(0.0255, 0.0433, -1.164, 1.143);
vec4 m3 = vec4(0.0055, 0.0037, 0.277, 0.382);

float sineZeroOneZero(float a) {
	return 1.0 - (0.5 + sin(1.5707963268 + a * 6.2831853072) * 0.5);
} 

float modulate(vec2 p, vec4 m, float time) {
	return cos(p.x * m.x + time * m.z) * sin(p.y * m.y + time * m.w);
} 

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord / iResolution.xy;

	float time = iTime * 1.0;
	float x = uv.x * 70.93;
	float y = uv.y * 60.55;
	vec2 p = vec2(x,y);

	float modulation = (
        modulate(p, m0, time) * 254.0 +
		modulate(p, m1, time) * 143.0 +
		modulate(p, m2, time) * 113.0 +
		modulate(p, m3, time) * 19.0
    ) * (sin(time * 0.1) * cos(time * 0.06) + 1.2) * 2.0;

	float blob = abs(modulation) * 0.004;

	float lightness = sineZeroOneZero(uv.x) * sineZeroOneZero(uv.y); 

	fragColor = vec4(
		floor(sin(blob * lightness * 10.0) * 1.1) * 0.8,
		floor(sin(blob * lightness * 10.0) * 1.01) * 0.5,
		floor(sin(blob * lightness * 10.0) * 1.5) * 1.0,
		1.0
	);
    // fragColor = vec4(fract(blob * 10.2),mod(blob*10.2, 2.1),0.0,1.0);
}
