/* originals https://www.shadertoy.com/view/XtSczV && https://www.shadertoy.com/view/MdXSzS   */
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdf( in vec2 p )
{
    p = mat2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime))*(p-vec2(3.0,0.0));
    return sdBox(p,vec2(0.7,0.25));
}

float map_correct( in vec2 p )
{
    // domain repetition
    const int   n = 80;
    const float b = 3.283185/float(n);
    float a = atan(p.y,p.x);
    float i = floor(a/b);

    float c1 = b*(i+1.0); vec2 p1 = mat2(cos(c1),-sin(c1),sin(c1), cos(c1))*p;
    float c2 = b*(i+1.0); vec2 p2 = mat2(cos(c2),-sin(c2),sin(c2), cos(c2))*p;

    // evaluate two SDF instances
    return min( sdf(p1), sdf(p2) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) - .5;
	float t = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + 1.0002)) * 2.2;
	float si = sin(t);
	float co = cos(t);
	mat2 ma = mat2(co, si, -si, co);

	float v1, v2, v3;
	v1 = v2 = v3 = 0.0;
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
 p *= 3.0;
    float d = map_correct( p );
	float s = 0.0;
	for (int i = 0; i < 90; i++)
	{
		vec3 p = s * vec3(uv, 0.0);
		p.xy *= ma;
		p += vec3(.22, .3, s - 1.5 - sin(iTime * .13) * .1)*d;
		for (int i = 0; i < 10; i++)	p = abs(p) / dot(p,p) - 0.659;
		v1 += dot(p,p) * .0015 * (1.8 + sin(length(uv.xy * 13.0) + .5  - iTime * .2));
		v2 += dot(p,p) * .0013 * (1.5 + sin(length(uv.xy * 14.5) + 1.2 - iTime * .3));
		v3 += length(p.xy*10.)*d * .0003;
		s  += 0.335;
	}

	float len = length(uv);
	v1 *= smoothstep(10.001, .0, len);
	v2 *= smoothstep(1.205, .0, len);
	v3 *= smoothstep(1.0009, .0, len);

	vec3 col = vec3( v3 * (1.5 + sin(iTime * .2) * .4),
					(v1 + v3) * .3,
					 v2) + smoothstep(0.2, .0, len) * .85 + smoothstep(.0, .6, v3) * .3;

	fragColor=vec4(min(pow(abs(col), vec3(1.2)), 1.0), 1.0);
}
