renderpass.code:
float random(in vec2 st){
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

mat2 rotate(float angle){
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

float lava(in vec2 coord, in float direction){
    float len;
    for(int i = 0;i  <3 ; i++){
        float noi = sin(floor(random(coord)*.9));
        len = length(coord)*length(coord)/9.;
        coord.x = coord.x - cos(coord.y + sin(len)) + cos(iTime / 9.0);
        coord.y = coord.y + cos(coord.y + cos(len)) + sin(iTime / 12.0);
        coord *= rotate(direction*iTime/10.);
    }
    return len;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 coord = 12.0 * (fragCoord.xy-iResolution.xy/2.0) / min(iResolution.x, iResolution.y);

    float n = random(fragCoord.xy/200.);

    float circle = sin(length(coord)*1.5);
    float circleMask = 1.-smoothstep(.1,.2, circle);
    float circleMask2 = smoothstep(.1,.1, circle);


    float lava1 = lava(coord,-1.)*circleMask;
    float lava2 = lava(coord+2.,1.)*circleMask2;
    float fx = (lava1+lava2);
    fragColor = vec4(cos((fx-.09)+.79), (cos(fx-.02))-.2, sin(cos(fx-.02))+.1, 1.0);
}
