vec3 circle(vec2 uv, vec2 pos, float rad)
{
    float d = length (pos - uv) - rad;
    if (d <= rad)
        return vec3(1.0,0.0,0.0);
    else
        return vec3(1.0,1.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy;
    vec2 center = iResolution.xy * 0.0;

    float radius = 0.05 * iResolution.y;
    vec2 velocity = vec2(1.0f, 1.0f);
    float m = mod(iTime * 250.0 / 500.0, 2.0);
    if (m < 1.0)
    {
        center = vec2(0.0);
        center += velocity * mod(iTime * 250.0, 500.0);
    }
    else
    {
        center = iResolution.xy;
        center -= velocity * mod(iTime * 250.0, 500.0);
    }

    fragColor = vec4(circle(uv,center,radius), 1.0);
}
