float GetDitherValue(vec2 InPosition, float DitheredTransitionFactor)
{
	float RandCos = cos(dot(floor(InPosition), vec2(347.83451793f,3343.28371963f)));
	float RandomVal = fract(RandCos * 1000.f);
	float RetVal = (DitheredTransitionFactor < 0.f) ?
		float(DitheredTransitionFactor + 1.f > RandomVal) :
		float(DitheredTransitionFactor < RandomVal);
	return (RetVal - 0.001f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float v = GetDitherValue(fragCoord, cos(iTime));

    // Output to screen
    fragColor = vec4(v, v, v, 1.0);


}
