float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv_noise = ((fragCoord * 2.0 - iResolution.xy) / iResolution.y) / fract(iTime / 5.0) * 5.0;
    vec3 col = vec3(1.0, 0.0, 0.0);

    // sky
    col += vec3(uv.y, uv.y, 0.0);

    // sun
    float d = length(uv - vec2(0, 0.5));
    d = smoothstep(0.25, 0.3, d);
    d = 1.0 - d;
    col += vec3(d);

    // noise
    col -= rand(uv_noise) * 0.25;

    // clouds
    vec3 clouds;

    for (float i = 0.0; i<10.0; i++) {
        uv -= 1.0;
        if (smoothstep(0.25, 0.75, texture(iChannel0, uv).r) > 0.0) {
            // clouds += (vec3(rand(uv)) * smoothstep(0.5, 1.0, texture(iChannel0, uv).r * -1.0) * 0.3);
            clouds += vec3(1.0);
        }else {
            clouds += vec3(0.1);
        }
        uv.x -= iTime * 0.025;
        uv.y -= sin(iTime * 0.025 * 0.5);
    }

    // output
    vec3 finalColor = col + (clouds * 0.025);
    fragColor = vec4(finalColor,1.0);
}
