float f1(float x) {
    vec2 v = pasr(x, 1.0, 1.0, 1.0, 1.0);
    return v.x + v.y;
}

float f2(float x) {
    vec2 v = pasr(x, 1.0, 1.0, 1.0, 1.0);
    return easeIn(v.x, 3.0) + easeOut(v.y, 3.0);
}

float f3(float x) {
    vec2 v = pasr(x, 0.2, 0.4, 0.6, 0.8);
    return v.x + easeIn(v.y, 3.0);
}

float f4(float x) {
    vec2 v = pasr(x, 0.5, 0.2, 0.5, 1.0);
    return easeInOut(v.x, 3.0) + easeInOut(v.y, 3.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // set up uvs
    vec2 uv0 = fragCoord / iResolution.y;
    float maxX = iResolution.x / iResolution.y;

    vec2 uv = uv0;
    float id = floor(uv.y / 0.25);
    uv.y = mod(uv.y, 0.25);
    uv *= 4.0;

    vec2 uvAnim = uv;
    uvAnim.x -= 4.0 * maxX - 1.0;
    uvAnim = 2.0 * uvAnim - 1.0;

    uv.x -= 1.0;
    uv *= 1.5;

    vec3 color = vec3(0.1);

    // grid
    float gt = 0.01;
    if (abs(uv.x) < 2.0 * gt) {
        color = vec3(0.7);
    } else if (mod(uv.x, 1.0) < gt || abs(uv.y) < gt) {
        color = vec3(0.3);
    }

    // curves
    float t = 0.5 * iTime;
    float x = uv.x + t;

    float valX;
    float valT;
    vec3 funcColor;
    if (id == 3.0) {
        valX = f1(x);
        valT = f1(t);
        funcColor = vec3(1.0, 1.0, 0.3);
    }
    else if (id == 2.0) {
        valX = f2(x);
        valT = f2(t);
        funcColor = vec3(0.3, 0.5, 1.0);
    }
    else if (id == 1.0) {
        valX = f3(x);
        valT = f3(t);
        funcColor = vec3(0.6, 1.0, 0.6);
    }
    else if (id == 0.0) {
        valX = f4(x);
        valT = f4(t);
        funcColor = vec3(1.0, 0.3, 0.3);
    }


    if (uvAnim.x > -1.0) {
        // draw circles
        float r = 0.1 + 0.7 * valT;
        float d = step(length(uvAnim), r);
        if (d > 0.0) {
            color = vec3(d) * funcColor;
        } else {
            color = vec3(0.1);
        }
    } else {
        // draw curves
        float d = 1.0 - step(0.03, abs(uv.y - valX));
        if (d > 0.0) {
            color = d * funcColor;
        }
    }

    fragColor = vec4(color, 1.0);
}

// periodic attack-sustain-release curve
// x = input value
// t1 = attack time
// t2 = sustain time
// t3 = release time
// t4 = reset time
vec2 pasr(float x, float t1, float t2, float t3, float t4) {
    float t = mod(x, t1 + t2 + t3 + t4);
    vec2 value = vec2(0.0);
    if (t < t1) {
        value.x = t / t1;
    } else if (t < t1 + t2) {
        value.x = 1.0;
    } else if (t < t1 + t2 + t3) {
        value.y = 1.0 - (t - t1 - t2) / t3;
    }
    return value;
}

float easeIn(float x, float q) {
    return pow(x, q);
}

float easeOut(float x, float q) {
    return 1.0 - pow(1.0 - x, q);
}

float easeInOut(float x, float q) {
    return x < 0.5
        ? pow(2.0 * x, q) / 2.0
        : 1.0 - pow(2.0 * (1.0 - x), q) / 2.0;
}
