vec4 hash41(float p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

// primitives mostly adapted from https://iquilezles.org/articles/distfunctions/
float halfspace(vec3 p)
{
    return p.z;
}

// adapted from https://iquilezles.org/articles/distfunctions/
float cylinder(vec3 p, float r, float h)
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sphere(vec3 p, vec3 c, float r)
{
    return length(p-c)-r;
}
/*
// wxmaxima:
// solve([sqrt((x_0-x_1)^2+(y_0-y_1)^2)=r_0+r_1,sqrt((x_1-x_2)^2+(y_1-y_2)^2)=r_1+r_2,sqrt((x_2-x_0)^2+(y_2-y_0)^2)=r_2+r_0],[r_0,r_1,r_2]);
// [[r_0=(sqrt((y_2-y_0)^2+(x_2-x_0)^2)-sqrt((y_1-y_2)^2+(x_1-x_2)^2)+sqrt((y_0-y_1)^2+(x_0-x_1)^2))/2,r_1=(-sqrt((y_2-y_0)^2+(x_2-x_0)^2)+sqrt((y_1-y_2)^2+(x_1-x_2)^2)+sqrt((y_0-y_1)^2+(x_0-x_1)^2))/2,r_2=-(-sqrt((y_2-y_0)^2+(x_2-x_0)^2)-sqrt((y_1-y_2)^2+(x_1-x_2)^2)+sqrt((y_0-y_1)^2+(x_0-x_1)^2))/2]]
