        vec3
        (
            +( sqrt(pow(y_2-y_0,2.0)+pow(x_2-x_0,2.0))-sqrt(pow(y_1-y_2,2.0)+pow(x_1-x_2,2.0))+sqrt(pow(y_0-y_1,2.0)+pow(x_0-x_1,2.0)))/2.0,
            +(-sqrt(pow(y_2-y_0,2.0)+pow(x_2-x_0,2.0))+sqrt(pow(y_1-y_2,2.0)+pow(x_1-x_2,2.0))+sqrt(pow(y_0-y_1,2.0)+pow(x_0-x_1,2.0)))/2.0,
            -(-sqrt(pow(y_2-y_0,2.0)+pow(x_2-x_0,2.0))-sqrt(pow(y_1-y_2,2.0)+pow(x_1-x_2,2.0))+sqrt(pow(y_0-y_1,2.0)+pow(x_0-x_1,2.0)))/2.0
        );
}
*/
vec3 radii(vec3 v0, vec3 v1, vec3 v2) // https://www.shadertoy.com/view/ml3fDn triangle to kissing spheres (jt)
{
    vec3 d =
        vec3
        (
            distance(v1,v2),
            distance(v2,v0),
            distance(v0,v1)
        );
    return
        vec3
        (
            +d.y-d.x+d.z,
            -d.y+d.x+d.z,
            +d.y+d.x-d.z
        )
        /
        2.0;
}

// NOTE: https://en.wikipedia.org/wiki/Cross_product exists only in three or seven dimensions
// possibly related to https://en.wikipedia.org/wiki/Hurwitz%27s_theorem_(composition_algebras)
float crossover(vec2 a, vec2 b) // could also be called perpdot (suggested by ollj)
{
    return a.x * b.y - a.y * b.x;
    //return determinant(mat2(a,b));
    //return dot(vec2(-a.y,a.x), b);
}

// Find circle tangent to three given circles
// https://en.wikipedia.org/wiki/Problem_of_Apollonius
vec3 apollonius_circle(vec2 a, vec2 b, vec2 c, float r, float s, float t, bool flag) // https://www.shadertoy.com/view/Msc3Wn Problem of Apollonius (jt)
{
    float rr = r * r;
    float ss = s * s;
    float tt = t * t;
    float aa = dot(a, a);
    float bb = dot(b, b);
    float cc = dot(c, c);

    vec4 u = vec4(2.0 * (a - b), 2.0 * (r - s), (aa - bb) - (rr - ss));
    vec4 v = vec4(2.0 * (a - c), 2.0 * (r - t), (aa - cc) - (rr - tt));

    vec2 p = u.xy * v.z - v.xy * u.z;
    vec2 q = u.xy * v.w - v.xy * u.w;

    float f = crossover(v.xy, u.xy);
    float ff = f * f;

    vec3 w =
        2.0
        *
        vec3
        (
    	    (dot(p, p) - ff),
    	    (dot(p, q) + f * crossover(p, a) + ff * r),
    	    f * crossover(q, a) + .5 * (ff * aa + dot(q, q) - ff * rr)
        );

    vec2 radii = (-w.y + vec2(1,-1) * sqrt(w.y * w.y - 2.0 * w.x * w.z)) / w.x;
    float select = flag ? radii.x : radii.y;
    return vec3((select * p.yx + q.yx) / vec2(f,-f), select);
}

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

vec4 combine(vec4 a, vec4 b)
{
    return a.w < b.w ? a : b;
}

vec4 map(vec3 p)
{
    //vec3 v0 = vec3(0,0,1);
    //vec3 v1 = vec3(1,0,0.5);
    //vec3 v2 = vec3(1,1,0);
    vec4 h = hash41(floor(iTime/1.0));
    vec3 v0 = vec3(0);
    vec3 v1 = vec3(vec2(0.5,0)+vec2(0.5,1)*h.xy,0);
    vec3 v2 = vec3(vec2(0,0.5)+vec2(1,0.5)*h.zw,0);
    vec3 c = (v0+v1+v2)/3.0;
    v0 -= c;
    v1 -= c;
    v2 -= c;

    vec3 r = radii(v0,v1,v2);
    //vec3 r = radii(v0.x,v0.y,v1.x,v1.y,v2.x,v2.y);

    vec3 d =
        vec3
        (
            //sphere(p, v0, r.x),
            //sphere(p, v1, r.y),
            //sphere(p, v2, r.z)
            cylinder(p - v0, r.x, 0.1),
            cylinder(p - v1, r.y, 0.1),
            cylinder(p - v2, r.z, 0.1)
        );

    vec3 a = apollonius_circle(v0.xy, v1.xy, v2.xy, r.x, r.y, r.z, false);
    vec3 b = apollonius_circle(v0.xy, v1.xy, v2.xy, r.x, r.y, r.z, true);

    vec4 result = vec4(vec3(0.5), halfspace(p));
    result = combine(result, vec4(vec3(0.25),cylinder(p - vec3(a.xy,0), a.z, 0.05)));
    result = combine(result, vec4(vec3(0.75),cylinder(p - vec3(b.xy,0), b.z, 0.05)));
    result = combine(result, vec4(1,0,0,d.x));
    result = combine(result, vec4(0,1,0,d.y));
    result = combine(result, vec4(0,0,1,d.z));
    return result;
}

// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal( vec3 p )
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ).w +
                      k.yyx*map( p + k.yyx*h ).w +
                      k.yxy*map( p + k.yxy*h ).w +
                      k.xxx*map( p + k.xxx*h ).w );
}

vec4 trace(vec3 ro, vec3 rd, float t0, float t1) // pass on running out of iterations
{
    // NOTE: Limited number of iterations to avoid stalling
    //       when ray passes closely (just above EPSILON)
    //       in parallel to a surface.
    uint i;
    vec4 t = vec4(0);
    for(t.w = t0, i = 0u; t.w < t1 && i < ITER_MAX; i++)
    {
        vec4 h = map(ro + rd * t.w);
        if(h.w < EPSILON)
            return t;
        t = vec4(h.xyz, t.w+h.w);
    }

    return t; // stop on running out of iterations
    //return vec4(0,0,0,t1); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).w < t1 ? 0.0 : 1.0;
}

// adapted from https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).w;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// adapted from https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).w;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec2 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi));
}

float checker(vec3 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi)*sin(p.z*pi));
}

vec3 material(vec3 p)
{
    return vec3(1.0); // TODO
    //return mix(vec3(0.25), vec3(0.75), checker(p*2.0+0.5));
}

void mainImage(out vec4 o, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0, 0,-2.5);
    vec3 rd = normalize(vec3(I.xy, 2.0)); // NOTE: omitting normalization results in clipped edges artifact

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    //ro.z += 1.0;

    vec3 color = vec3(1);
    vec4 result = trace(ro, rd, 0.0, DIST_MAX);
    if(result.w < DIST_MAX)
    {
        float d = result.w;
        vec3 dst = ro + rd * d;
        vec3 n = normal(dst);

        //color *= (n * 0.5 + 0.5);
        color = result.xyz;
        color *= material(dst);

        vec3 lightdir = normalize(vec3(3,2,1));
        vec3 ambient = vec3(0.4);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            //brightness *= shadow(ro + rd * d + n * 0.01, lightdir, 0.0, DIST_MAX);
            brightness *= softshadow(ro + rd * d + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0);
        color *= (ambient * calcAO(dst, n) + brightness);

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular;
        }

        vec3 fog_color = vec3(0.2);
        color = mix(fog_color, vec3(color), exp(-pow(d/20.0, 2.0))); // fog
    }
    else
    {
        color *= mix(vec3(0,0.5,0.5),vec3(0,0,1),abs(-rd.z)); // sky
        color = vec3(0);
    }

    color = tanh(color); // roll-off overly bright colors
    color = sqrt(color); // approximate gamma
    o = vec4(color, 1);
}
