#define TAU    6.283185
#define ss(v)  smoothstep(dFdx(p.x), -dFdx(p.x),v)
#define _(f,v) (f(v)+1.)*.5

vec3 Fan(vec2 p, float q) 
{
    q = mod(atan(p.x, p.y) + q/2., q) - q/2.;
    return vec3(length(p) * vec2(sin(q), cos(q)), floor(TAU*(atan(p.x, p.y)/TAU + .5)
                +q*mix(15.,55.,_(sin, iTime)))*dot(vec2(cos(iTime), sin(iTime)), p));
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R  = iResolution.xy,
         p  = (U-R*.5)/R.y;
    vec3 Q = Fan(p, TAU/11.);

    float d = sin(iTime*5.+log(abs(Q.z*Q.z*Q.z+3.))+(atan(p.x, p.y)/TAU + .5))+3.;

    O = mix(vec4(.4,.4,.5,1)*1.-length(p)/3.,
    vec4(1,vec2(1.-d/6.*_(sin,iTime*2.), d/4.*_(cos,iTime*.7)),1),ss(length(Q.xy-vec2(0,d)*.1)-.05));
}
