// Fork of "Estudo de cores aleatorias " by Elsio. https://shadertoy.com/view/dldyRn
// 2023-11-02 14:15:57

#define cor(a) (cos(a * 6.3 + vec3(0, 23., 21.) * a) * .5 + .5)

// tks Blackle!
#define FBI floatBitsToInt
float hash(vec2 p) {
    float a = p.x, b = p.y;
    int x = FBI(cos(a)) ^ FBI(a),
        y = FBI(cos(b)) ^ FBI(b);
    return float((x * x + y) * (y * y - x) + x) / 2.14e9;
}

#define EXPLOSION_RATE     8.0

void mainImage(out vec4 o, in vec2 u) {
    vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;
    mouse -= 0.5;

    vec2 uvTest = (gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y;

    vec2 R = iResolution.xy; o *= 0.;
    u = 36. * (u - R / 2.) / R.y;

    float time = floor(iTime*EXPLOSION_RATE);

    vec2 time2 = vec2(sign(mouse.x)*sign(uvTest.x)*time, sign(mouse.y)*sign(uvTest.y)*time);

    vec3 p  = vec3(u, 1),
         q  = fract(p) - .5,
         id = floor(p) - .5;

    float z = 1. - length(q.xy);

    o.rgb += z * cor(hash(id.xy-time2));
}
