void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //needed for anti aliasing ?
    float n_ratio_1to_iresx = 1./ iResolution.x;

    vec2 o_trn_fc_nor = fragCoord.xy / iResolution.xy;
    vec2 o_trn_mo_nor = iMouse.xy / iResolution.xy;

    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    vec2 o_trn_mo_nor_offset = (o_trn_mo_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    o_trn_fc_nor_offset*=2.5;

    float n_its = 50.;
    float n_tau = 6.2831;
    float n_dist_prod = 1.;
    float n_dist_prod2 = 1.;
    float n_dist_min = 1.;
    float n_it_nor_dmin = 0.;
    for(float n_it = 0.; n_it< n_its; n_it+=1.){
        float n_it_nor = n_it/ n_its;
        float n_radius = cos(iTime+n_it_nor*n_tau*2.);
        float n_ang_nor = n_tau * n_it_nor;
        vec2 o_p = vec2(
            sin(n_ang_nor)*n_radius,
            cos(n_ang_nor)*n_radius
        );
        float n_dist = length(o_trn_fc_nor_offset - o_p);
        if(n_dist < n_dist_min){
            n_it_nor_dmin = n_it_nor;
        }
        n_dist_prod *= n_dist;
        n_dist_prod2 *= pow(n_dist, 1./10.);

        n_dist_min = min(n_dist_min, n_dist);
    }

    float n_aa = n_ratio_1to_iresx*200.;
    float n_th = 0.5;
    float nds = smoothstep(n_th, n_th+n_aa, n_dist_prod);
    fragColor = vec4(1.-n_dist_prod2);

    if(o_trn_fc_nor.y > (1./3.)*1.){
        vec4 o_col = vec4(n_it_nor_dmin, 0.,0.,1.);
        fragColor = vec4(o_col*(1.-nds));
    }
    if(o_trn_fc_nor.y > (1./3.)*2.){
        fragColor = vec4(1.-n_dist_min);
    }
    if(o_trn_fc_nor.y > (1./3.)*3.){
        fragColor = vec4(1.-n_dist_prod);
    }
}
