vec3 pal(float t) {return .7+.3*cos(6.28*(vec3(.7,.2,.7)*t+vec3(.0,.3,.7)));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    uv*=4.;
    vec4 d = vec4(100.,0,0,0);
    float t=iTime;
    for(float i=0.,im=50.;i++<im;){
        vec4 p;
        float t = t*(.1+(i/im));
        vec2 uv = uv+vec2(sin(i*5.+t),cos(i*7.+t*.33))*2.;
        p.x = length(pow(uv,vec2(4.)))-.00000001;
        p.x = pow(p.x,1./(4.-fract(iTime+i*i/im)*3.));
        p.yzw = pal(i/im);
        d = p.x < d.x  ? p:d;

    }
    //d.x = .05/(abs(d.x)-.001);
    d.x = smoothstep(fwidth(d.x),0.,abs(d.x+dot(sin(uv*100.),cos(uv.yx*400.))*.2)-.8);
    vec3 col =d.x*d.yzw;
    fragColor = vec4(sqrt(col),1.0);
}
