// 287 chars by @Elsio (and remarks of @timestamp)
#define r mat2(cos(iTime * .1 + q.z + vec4(0, 11, 33, 0)))

void mainImage(out vec4 O, vec2 u){
    vec3 p, q, D = iResolution; 
    float i, d = D.x;
    D = vec3(u, 2. * D.y) / d / 120.;

    D.xz *= r;
    for(D.yz *= r; i++ < 2e2  &&  d > .12; q = p += d * D) 
        for(int j; j++ < 5; q *= 2.3)
            d = length(q.xy = abs(q.xy * r) - .2); 

    O = O-O + pow(60. / i, .2) - .5;
    O.z += length(q) * .9 - .3;    
}







