float map(in vec3 p) {
    p = mod(p + 0.5, 1.0) - 0.5;
    return length(p) - 0.25;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    vec3 ro = vec3((iMouse.xy - 0.5 * iResolution.xy) / iResolution.y, 5.0);

    vec3 fd = -normalize(ro);
    vec3 rt = normalize(vec3(-fd.z, 0.0, fd.x));
    vec3 up = cross(rt, fd);

    vec3 rd = normalize(uv.x * rt + uv.y * up + fd);
    bool inside = map(ro) < 0.0;
    float total = 0.0;
    float t = 0.0;
    for (int i=0; i < 100; i++) {
        if (inside) {
            total += 0.05;
            t += 0.05;
            if (map(ro + rd * t) >= 0.0) inside = false;
        }

        else {
            t += map(ro + rd * t);
            if (map(ro + rd * t) < 0.0) inside = true;
        }

        if (total > 1.0) break;
    }

    color += total;
    fragColor = vec4(color, 1.0);
}
