void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    float radius = length(uv);
    float angle = atan(uv.y, uv.x);
    vec3 color = vec3(0.0);
    float t = iTime * 0.1;
    float gradientCount = 3.0;
    float gradientIndex = mod(floor(t), gradientCount);
    float gradientT = fract(t);
    if (gradientIndex == 0.0) {
        color = mix(vec3(0.0, 0.0, 0.0), vec3(1.0, 0.0, 0.0), gradientT);
    } else if (gradientIndex == 1.0) {
        color = mix(vec3(1.0, 0.0, 0.0), vec3(1.0, 1.0, 0.0), gradientT);
    } else if (gradientIndex == 2.0) {
        color = mix(vec3(1.0, 1.0, 0.0), vec3(1.0, 1.0, 1.0), gradientT);
    }
    color *= 1.0 - smoothstep(0.4, 0.5, radius);
    color += vec3(1.0, 1.0, 1.0) * smoothstep(0.4, 0.5, radius) * (0.5 + 0.5 * sin(5.0 * angle + 2.0 * iTime));
    fragColor = vec4(color, 1.0);
}
