//Inspired from https://youtu.be/Myeatl2Q3wM

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    vec3 Video = texture(iChannel0, vec2(uv - vec2(.5, 0.))).rgb;

    // Time varying pixel color
    vec3 col = vec3(0.);

    if (length(vec3(0., .8, 0.) - Video) > .475) {
        col = Hash32(uv * 100. + mod(floor(iTime * 30.), 30.) * 10.);
    } else {
        col = Hash32(uv * 100. + 1.);
    }

    // Output to screen
    fragColor = vec4(col, 1.);
}

//from Dave_Hoskins
vec3 Hash32(vec2 p)
{
    p *= 23.37246147;
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
