void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/min(iResolution.y,iResolution.x);
    p *= 1.0 + 0.2*sin(length(p)*10.0 - iTime*2.0);
    float pattern = 0.5 + 0.5*sin(p.x*10.0 + iTime*2.0) * cos(p.y*10.0 + iTime*2.0);
    float noise = 0.1*sin(p.x*30.0 + p.y*20.0 + iTime*5.0);
    vec3 finalColor = vec3(pattern + noise);
    fragColor = vec4(step(0.5,finalColor.r));
}
