void mainImage( out vec4 O, vec2 I)
{
    vec3 d=normalize(vec3(I+I,O*=0.)-iResolution.xyy),
    p,q,s;
    for(p.z=35.; O.w++<1e2; 
    O.rgb+=(1.-d)*min(
            s = length(q)-2e1+
            sin(q.x/6.)*2.+
            9./max(q.y+5.,.1)+
            9./(4.+q*q).z+
            .6*(s+=dot(cos(q),sin(q.yzx))-s)*s
        ,.1)/dot(p+=d*s*.3,p)*6e1)
        q = p+9.,
        q.xz = p.xz*mat2(cos(iTime+vec4(0,11,33,0)));
}
