//Buffer A is where it is at
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord.xy/iResolution.xy;


    vec2 texel = 1./iResolution.xy;
    vec4 total_color;
    for (int i=0;i<5;i++){
        float fi = float(i)-2.;
        for (int j=0;j<5;j++){ 
            float fj = float(j)-2.;
            vec4 color = texture(iChannel0, 
                uv + vec2( texel.x*fi,texel.y*fj )  );
            total_color += color * gk1s[i*5 + j];
        }
    }
    fragColor = total_color;


}

float num_iter, objid, glow=0., glow2=0., mmaxDist=50.;
vec3 ifs_color, ro, light_dir;

mat2 rot(float an) {return mat2(cos(an),-sin(an),sin(an),cos(an));}

float random(vec2 p) {return fract( sin( dot( p, vec2(12., 90.)))* 1e4 );}


float de(vec3 p) {
  vec3 origP= p;  
  ifs_color = vec3(0.);  
  if (p.z < -.6) { objid=3.; return 0.3;}   //sky 
  float nn = 1.6;
  p.xz = mod(p.xz  + nn, 2.*nn) -nn; p.yz = mod(p.yz  + nn, 2.*nn) -nn;  
  float shellNum =  p.z/3.;  
  vec4 q0 = vec4 (p, 2.), q = q0; float i=0.;
  for (; i<params[1]; i ++) {
    q.xyz = clamp (q.xyz, -1.-params[5], 1.+params[5]) * (2.+params[4]) 
            - (1.+params[3])*q.xyz; 
    q = q * (2.52+params[2] - shellNum  ) 
        / clamp (  max( dot(q.yz,q.yz), max(dot (q.xz, q.xz),  dot(q.xy,q.xy) ) ), 
        (-1.+params[4])-params[0], 1.+params[0]) + q0;
    if (q.x*q.y > 0. ) ifs_color.x ++;
    if (q.y*q.z > 0. ) ifs_color.y ++;
    if (q.z*q.x > 0. ) ifs_color.z ++;  
  } 
  ifs_color /= i;
  objid = 0.;
  return max( length (q.xyz) / q.w, -(length(origP-ro)-params[7] ) ) ; 
}

float march(vec3 ro, vec3 rd, float mx) {

    float t=0.,eps = 1e-5, distfac=50., hitThreshold = eps;
    for(int i = 0; i < 200; i++) {
        vec3 pos = ro + rd*t;
        float d = de(pos);      
        if (objid == 1. || objid == 3.) { return d; }       
        if (d < hitThreshold || t >= mx) break;
        t += d;
        hitThreshold = eps*(1.+t*t*distfac);       
        float zz = pos.y - light_dir.y;  zz*=zz;
        glow += exp(-max( 8.*(1.-exp(-zz*4.))-d,0.)/10.);        
        float zz2 = ro.z - 0. - pos.z; zz2*=zz2;        
        glow2 += exp( -max(1.-d,0.)/300.);        
    }
    return t;
}

vec3 render(vec3 ro, vec3 rd) {
    float tt = mod(iTime,6.), tf = tt*tt; 
    vec3 col = vec3(0.);
    light_dir = ro ;  light_dir.y += tf;
    vec3 color = vec3( params[27], params[28], params[29]);
    float t = march(ro, rd, mmaxDist);
    vec3 glowCol;
    vec3 pos = ro + rd*t; 
    vec3 glowSource = light_dir;
    float glowStr = exp( -length(pos.y - glowSource.y) / 4.);
    glow *= glowStr; 
    float glowStr2 = (exp( -length( pos - ro + vec3(0,0,0.) ) / 6.));
    glow2 *= glowStr2;          
    vec3 addColor = cos(ifs_color*2.);      
    color = addColor;
    color*=color;  
    glowCol = (color*color);
    if (objid == 1.) {
        return vec3(0,0,.5) + 8e-9*pow(glow2,7.)*vec3(t*t,.001,0.); 
    }
    else if (objid == 2.) {
        return vec3(0.,.2,0.) - 8e-9*pow(glow2,7.)*vec3(0.,0.,t*t); 
    }
    else if (objid == 3. ) {                     
        return    
            8e-5*pow(glow2,3.)*vec3(0.,0.,.2+t*t*t)
            + 3e-9*pow(glow,8.)*vec3(0.2,.02,-.03)*glowCol 
            ;     
    }
    return   4e-12*pow(glow2,9.)*glowCol + 1e-10*pow(glow,11.)*vec3(0.05,.03,.001)*glowCol
        + .01*(cos(pos.x/15.+iTime*1.7))*1e-9*glowCol*(pos.z < (-.2 + .2*random(pos.xy)  ) ? pow(glow2,11.) :  0.)
        ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p  = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;    
    ro = vec3(0.,1.5, -.1 ) ;     
    vec3 rd = normalize( vec3(p,1.5 * (1.+.2*sin(iTime/2.) ))) ;      
    if (iMouse.w != 0.) {
        vec2 an = 6.*(iMouse.xy/iResolution.xy-.5);
        vec2 cc=cos(an), ss=sin(an);
        //rd.xz *= mat2(cc.x,-ss.x,ss.x,cc.x);
        rd.yz *= mat2(cc.y,-ss.y,ss.y,cc.y);
    }

    rd.yz *= rot(-1.57); //the interesting view is in the xy plane
    ro.y += iTime/5.;
    rd.xy *= rot(.9);

    vec3 col = clamp(render(ro, rd),1e-4,1e6);
    col = pow ( ( 1. - exp(-.4 * (1.+params[16]) * col) ) , vec3(.3) );

    fragColor = vec4( col, 1.);

}
