void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(0.5, 0.0);

    vec4 flameColor = vec4(0.8, 0.6, 0.22, 0.);

    float distY = length(uv.y-center.y);
    float distX = length(uv.x-center.x);

    float falloff = pow(distX, .05);
    float brightnessFlicker = distX+max(abs(sin(iTime*800.)),0.9) * falloff ;

    float distT = (distY * pow(1.-distX, 20.))*9.;

    fragColor = vec4(distT, distT, distT, 0.0) * flameColor * brightnessFlicker;
}
