void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float e,R,s,t = iTime;
    vec3 q,p,d=vec3(fragCoord.xy/iResolution.xy+vec2(-.5,.5),1);
    vec4 o;
    q.zy--;

    for(float i=0.;i<79.;i++){
        o += .02-exp(-e*100.)*.04*vec4(R+R,vec3(1./exp(R)));
        p=q+=d*e*R*.1;
        p = vec3(log2(R=length(p))-t,exp2(R-p.z/R),t-atan(p.x,p.y)*4.+sin(iTime));
        e=--p.y;

        for(s=2.5; s<20.; s+=s) 
            e+=dot(cos(p.xz*s),
            sin(p.xx*s+R+.8))/s;
    }

    fragColor = o;
}
